-- Add diabetes dx data to temporary table insert into temp_ncd_diagnosis (ncd_dx_obs_id, encounter_id, person_id, location_id, obs_datetime, creator) select obs_id, encounter_id, person_id, location_id, obs_datetime, creator from obs where voided = 0 and concept_id = (select concept_id from concept_name where name like 'Type of diabetes diagnosis' and locale = 'en' and locale_preferred = 1 and voided = 0) and encounter_id IN (select encounter_id from encounter where encounter_type IN (select encounter_type_id from encounter_type where name like 'NCD%'));
-- Create obsgroup; Use obs_group_id on the parent obs temporarily to hold the ncd diagnosis child obs_id insert into obs (encounter_id, person_id, location_id, obs_datetime, creator, concept_id, comments, obs_group_id, date_created, uuid) select encounter_id, person_id, location_id, obs_datetime, creator, (select concept_id from concept_name where name like 'Visit Diagnoses' and locale = 'en' and locale_preferred = 1 and voided = 0), 'ncd diagnosis obsgroup', obs_id, now(), uuid() from obs where voided = 0 and concept_id = (select concept_id from concept_name where name like 'Type of diabetes diagnosis' and locale = 'en' and locale_preferred = 1 and voided = 0) and encounter_id IN (select encounter_id from encounter where encounter_type IN (select encounter_type_id from encounter_type where name like 'NCD%'));
-- Add obsgroup id to the temporary table update temp_ncd_diagnosis, obs set temp_ncd_diagnosis.obs_group_id = obs.obs_id, obs.obs_group_id = null where obs.voided = 0 and obs.comments = 'ncd diagnosis obsgroup' and temp_ncd_diagnosis.ncd_dx_obs_id = obs.obs_group_id;
-- Add obsgroup id to obs table update temp_ncd_diagnosis, obs set obs.obs_group_id = temp_ncd_diagnosis.obs_group_id where obs.voided = 0 and temp_ncd_diagnosis.ncd_dx_obs_id = obs.obs_id;
-- NCD dx order (create a new diagnosis order obs) insert into obs (person_id, concept_id, value_coded, encounter_id, obs_datetime, location_id, obs_group_id, comments, creator, date_created, uuid) select person_id, (select concept_id from concept_name where name like 'Diagnosis order' and locale = 'en' and locale_preferred = 1 and voided = 0), (select concept_id from concept_name where name like 'Secondary' and locale = 'en' and locale_preferred = 1 and voided = 0), encounter_id, obs_datetime, location_id, obs_group_id, 'ncd dx inserted secondary order', creator, now(), uuid() from temp_ncd_diagnosis;
-- NCD dx (change the concept for the diagnosis) update obs set concept_id = (select concept_id from concept_name where name like 'Diagnosis' and locale = 'en' and locale_preferred = 1 and voided = 0), comments = (select Concat('ncd diagnosis updated ',sysdate()) from dual) where concept_id = (select concept_id from concept_name where name like 'Type of diabetes diagnosis' and locale = 'en' and locale_preferred = 1 and voided = 0) and voided = 0 and encounter_id IN (select encounter_id from encounter where encounter_type IN (select encounter_type_id from encounter_type where name like 'NCD%'));
