changeSet.sql:
drop table if exists temp_ncd_diagnosis;
create table temp_ncd_diagnosis ( id INT not null auto_increment primary key, encounter_id int(11) not NULL, person_id int(11) not NULL, location_id int(11) not NULL, ncd_dx_obs_id int(11) not NULL, obs_group_id int(11) default NULL, obs_datetime datetime default NULL, creator int(11) not NULL );
-- Add dx data to temporary table insert into temp_ncd_diagnosis (ncd_dx_obs_id, encounter_id, person_id, location_id, obs_datetime, creator) select obs_id, encounter_id, person_id, location_id, obs_datetime, creator from obs where voided = 0 and concept_id = (select concept_id from concept_name where name like 'Diagnosis' and locale = 'en' and locale_preferred = 1 and voided = 0) and value_coded != (select concept_id from concept_name where name like 'Other' and locale = 'en' and locale_preferred = 1 and voided = 0) and obs_group_id is NULL and encounter_id IN (select encounter_id from encounter where encounter_type IN (select encounter_type_id from encounter_type where name like 'NCD%'));
-- Create obsgroup; Use obs_group_id temporarily to hold the ncd diagnosis obs_id insert into obs (encounter_id, person_id, location_id, obs_datetime, creator, concept_id, comments, obs_group_id, date_created, uuid) select encounter_id, person_id, location_id, obs_datetime, creator, (select concept_id from concept_name where name like 'Visit Diagnoses' and locale = 'en' and locale_preferred = 1 and voided = 0), 'ncd diagnosis obsgroup', obs_id, now(), uuid() from obs where voided = 0 and concept_id = (select concept_id from concept_name where name like 'Diagnosis' and locale = 'en' and locale_preferred = 1 and voided = 0) and value_coded != (select concept_id from concept_name where name like 'Other' and locale = 'en' and locale_preferred = 1 and voided = 0) and obs_group_id is NULL and encounter_id IN (select encounter_id from encounter where encounter_type IN (select encounter_type_id from encounter_type where name like 'NCD%'));
-- Add obsgroup id to the temporary table update temp_ncd_diagnosis, obs set temp_ncd_diagnosis.obs_group_id = obs.obs_id, obs.obs_group_id = null where obs.voided = 0 and obs.comments = 'ncd diagnosis obsgroup' and temp_ncd_diagnosis.ncd_dx_obs_id = obs.obs_group_id;
-- Add obsgroup id to obs table update temp_ncd_diagnosis, obs set obs.obs_group_id = temp_ncd_diagnosis.obs_group_id, obs.comments = (select Concat('ncd diagnosis updated ',sysdate()) from dual) where obs.voided = 0 and temp_ncd_diagnosis.ncd_dx_obs_id = obs.obs_id and obs.concept_id = (select concept_id from concept_name where name like 'Diagnosis' and locale = 'en' and locale_preferred = 1 and voided = 0);
-- NCD dx order (create a new diagnosis order obs) insert into obs (person_id, concept_id, value_coded, encounter_id, obs_datetime, location_id, obs_group_id, comments, creator, date_created, uuid) select person_id, (select concept_id from concept_name where name like 'Diagnosis order' and locale = 'en' and locale_preferred = 1 and voided = 0), (select concept_id from concept_name where name like 'Secondary' and locale = 'en' and locale_preferred = 1 and voided = 0), encounter_id, obs_datetime, location_id, obs_group_id, 'ncd dx inserted secondary order', creator, now(), uuid() from temp_ncd_diagnosis;
drop table if exists temp_ncd_diagnosis;

create temporary table imported_items_to_delete select i1.imported_item_id from metadatasharing_imported_item i1, metadatasharing_imported_item i2 where i1.uuid = i2.uuid and i1.imported_item_id != i2.imported_item_id and IFNULL(i1.date_imported, '1000-01-01') < i2.date_imported;
insert into imported_items_to_delete select i1.imported_item_id from metadatasharing_imported_item i1, metadatasharing_imported_item i2 where i1.uuid = i2.uuid and i1.imported_item_id != i2.imported_item_id and i1.date_imported is null and i2.date_imported is null and i1.imported_item_id < i2.imported_item_id;
delete from metadatasharing_imported_item where imported_item_id in (select distinct imported_item_id from imported_items_to_delete);

