htmlform.script:
		if(jQuery){
	 jQuery(document).ready(function(){
		if ( jQuery.browser.msie ) {
			jQuery(":checkbox").click(function(){
				 jQuery(this).change();
		 });
	 }
	 // Enabled et disabled input type text
	 jQuery(".enableDisable").each(function(){
     var group = jQuery(this);
     function disableFn(){
         group.children("#disabled").fadeTo(250,0.33);
         group.children("#disabled").find(":checkbox").attr("checked",false); //uncheck
         group.children("#disabled").find("input[type$='text']").val("");
         group.children("#disabled").find("input").attr("disabled",true);  //disable
     }
	 function enableFn(){
		 group.children("#disabled").fadeTo(250,1);
		 group.children("#disabled").find("input").attr("disabled",false);
	 }
	 disableFn();
	 jQuery(this).children("#trigger").find(":checkbox:first").change(function(){
		 var checked = jQuery(this).attr("checked");
		 if(checked == true){
			  //enableFn();
			  disableFn();
		 }else{
			 disableFn();
		 }
	 });
	});

	 //Fin enabled disabled input type text

	 jQuery(".checkboxGroup").each(function(){
		 var group = jQuery(this);
		 var uncheckAll = function(){
			 group.find("input[type$='checkbox']").attr("checked",false);
			 group.find("input[type$='checkbox']").change();
		 }

	//Debut checkbox examen Clinique
	group.find("#normal1").find("input[type$='checkbox']").click(function(){
			group.find("#anormal1").find("input[type$='checkbox']").attr("checked",false);
			group.find("#anormal1").find("input[type$='checkbox']").change();
			group.find("#pasconduit1").find("input[type$='checkbox']").attr("checked",false);
			group.find("#pasconduit1").find("input[type$='checkbox']").change();
		 }
	);
	group.find("#anormal1").find("input[type$='checkbox']").click(function(){
			group.find("#normal1").find("input[type$='checkbox']").attr("checked",false);
			group.find("#normal1").find("input[type$='checkbox']").change();
			group.find("#pasconduit1").find("input[type$='checkbox']").attr("checked",false);
			group.find("#pasconduit1").find("input[type$='checkbox']").change();
		 }
	);
	group.find("#pasconduit1").find("input[type$='checkbox']").click(function(){
			group.find("#normal1").find("input[type$='checkbox']").attr("checked",false);
			group.find("#normal1").find("input[type$='checkbox']").change();
			group.find("#anormal1").find("input[type$='checkbox']").attr("checked",false);
			group.find("#anormal1").find("input[type$='checkbox']").change();
		 }
	);
	// FIN examen Clinique

	//Debut ANTÉCEDENTS MÉDICAUX ET SYMPTÔMES ET DIAGNOSTICS ACTUELS
		group.find("#actif").find("input[type$='checkbox']").click(function(){
			group.find("#gueri").find("input[type$='checkbox']").attr("checked",false);
			group.find("#gueri").find("input[type$='checkbox']").change();
		 }
	);
	group.find("#gueri").find("input[type$='checkbox']").click(function(){
			group.find("#actif").find("input[type$='checkbox']").attr("checked",false);
			group.find("#actif").find("input[type$='checkbox']").change();
		 }
	);
	//Fin ANTÉCEDENTS MÉDICAUX ET SYMPTÔMES ET DIAGNOSTICS ACTUELS


      return true;
     });
