ifMode.script:
jq(function() {
    $('#condition-status > input:radio').each(function(idx, it){
        if($(it).val() == 'active'){
            $(it).click();
            $(it).prop("checked", true);
            $(it).unbind('click').click(function () {
                $(this).prop("checked", true);
                return false;
            });
        } else {
            $(it).attr('disabled',true).hide();
            $('label[for = '+$(it).attr('id')+']').hide();
        }
    });
});
